module.exports = {
    configure : function(config, proc, done) {
        if (proc.isMaster) {
            setTimeout(function() {
                console.log('luster-async extension configured on master process');
                console.log('param1 = %s', config.get('param1'));
                console.log('param2 = %s', config.get('param2'));
                done();
            }, 1000);
        } else {
            setTimeout(function() {
                console.log('luster-async extension configured on worker process #%s', proc.wid);
                console.log('param1 = %s', config.get('param1'));
                console.log('param2 = %s', config.get('param2'));
                done();
            }, 5000);
        }
    }
};
