var cluster = require('cluster');

module.exports = {
    configure : function(config, proc) {
        if (proc.isMaster) {
            console.log('luster-simple extension configured on master process');
            console.log('param1 = %s', config.get('param1'));
            console.log('param2 = %s', config.get('param2'));
        } else {
            console.log('luster-simple extension configured on worker process #%s', cluster.worker.id);
            console.log('param1 = %s', config.get('param1'));
            console.log('param2 = %s', config.get('param2'));
        }
    }
};
